// Initialize database
let database = JSON.parse(localStorage.getItem('betterdeed_db')) || {
    videos: [],
    password: 'publisher123' // Default password
};

// DOM Elements
const videoGrid = document.getElementById('videoGrid');
const publisherBtn = document.getElementById('publisherBtn');
const publisherModal = document.getElementById('publisherModal');
const closePublisherModal = document.getElementById('closePublisherModal');
const loginBtn = document.getElementById('loginBtn');
const loginSection = document.getElementById('loginSection');
const publisherControls = document.getElementById('publisherControls');
const uploadForm = document.getElementById('uploadForm');
const videoList = document.getElementById('videoList');

// Initialize the app
document.addEventListener('DOMContentLoaded', function() {
    renderVideos();
    setupEventListeners();
});

function setupEventListeners() {
    // Publisher panel toggle
    if (publisherBtn) {
        publisherBtn.addEventListener('click', () => {
            publisherModal.style.display = 'flex';
        });
    }

    if (closePublisherModal) {
        closePublisherModal.addEventListener('click', () => {
            publisherModal.style.display = 'none';
        });
    }

    // Login functionality
    if (loginBtn) {
        loginBtn.addEventListener('click', function() {
            const password = document.getElementById('publisherPassword').value;

            if (password === database.password) {
                loginSection.style.display = 'none';
                publisherControls.style.display = 'block';
                renderVideoList();
            } else {
                alert('Incorrect password!');
            }
        });
    }

    // Video upload
    if (uploadForm) {
        uploadForm.addEventListener('submit', function(e) {
            e.preventDefault();

            const title = document.getElementById('videoTitle').value;
            const thumbnailFile = document.getElementById('thumbnailFile').files[0];
            const videoFile = document.getElementById('videoFile').files[0];

            if (!videoFile) {
                alert('Please select a video file');
                return;
            }

            // Process video to get thumbnail if not provided
            let thumbnailUrl;
            if (thumbnailFile) {
                thumbnailUrl = URL.createObjectURL(thumbnailFile);
            } else {
                // Create thumbnail from video
                const video = document.createElement('video');
                const canvas = document.createElement('canvas');
                const ctx = canvas.getContext('2d');

                video.src = URL.createObjectURL(videoFile);
                video.onloadedmetadata = () => {
                    canvas.width = video.videoWidth;
                    canvas.height = video.videoHeight;
                    video.currentTime = 0;
                };

                video.onseeked = () => {
                    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
                    thumbnailUrl = canvas.toDataURL('image/jpeg');

                    // Create video object
                    const videoUrl = URL.createObjectURL(videoFile);
                    addVideo(title, thumbnailUrl, videoUrl);
                };

                return;
            }

            const videoUrl = URL.createObjectURL(videoFile);
            addVideo(title, thumbnailUrl, videoUrl);
        });
    }
}

function addVideo(title, thumbnailUrl, videoUrl) {
    const newVideo = {
        id: Date.now().toString(),
        title,
        thumbnailUrl,
        videoUrl,
        views: 0,
        timestamp: new Date().toISOString()
    };

    database.videos.unshift(newVideo);
    saveDatabase();

    alert('Video uploaded successfully!');
    uploadForm.reset();
    renderVideos();
    renderVideoList();
}

function renderVideos() {
    if (!videoGrid) return;

    videoGrid.innerHTML = database.videos.map(video => `
        <a href="player.html?id=${video.id}" class="video-card">
            <img src="${video.thumbnailUrl}" class="video-thumbnail" alt="${video.title}">
            <div class="video-info">
                <h3>${video.title}</h3>
                <p>${video.views} views</p>
            </div>
        </a>
    `).join('');
}

function renderVideoList() {
    if (!videoList) return;

    videoList.innerHTML = database.videos.map(video => `
        <div class="video-item">
            <span>${video.title}</span>
            <button class="btn delete-btn" onclick="deleteVideo('${video.id}')">Delete</button>
        </div>
    `).join('');
}

function deleteVideo(id) {
    if (confirm('Are you sure you want to delete this video?')) {
        const video = database.videos.find(v => v.id === id);
        if (video) {
            URL.revokeObjectURL(video.videoUrl);
            if (video.thumbnailUrl.startsWith('blob:')) {
                URL.revokeObjectURL(video.thumbnailUrl);
            }
        }

        database.videos = database.videos.filter(v => v.id !== id);
        saveDatabase();
        renderVideos();
        renderVideoList();
    }
}

function saveDatabase() {
    localStorage.setItem('betterdeed_db', JSON.stringify(database));
}

// Make deleteVideo available globally
window.deleteVideo = deleteVideo;

js.player.js:
// Get video ID from URL
const urlParams = new URLSearchParams(window.location.search);
const videoId = urlParams.get('id');

// Load video data
const database = JSON.parse(localStorage.getItem('betterdeed_db')) || { videos: [] };
const video = database.videos.find(v => v.id === videoId);

if (!videoId || !video) {
    window.location.href = './';
} else {
    // Set up player
    const videoPlayer = document.getElementById('videoPlayer');
    const videoTitle = document.getElementById('videoTitle');
    const videoViews = document.getElementById('videoViews');

    videoPlayer.src = video.videoUrl;
    videoTitle.textContent = video.title;
    videoViews.textContent = `${video.views} views`;

    // Increment view count
    video.views = (video.views || 0) + 1;
    localStorage.setItem('betterdeed_db', JSON.stringify(database));

    // Quality selector (simulated)
    document.getElementById('qualitySelect').addEventListener('change', function() {
        console.log('Quality changed to:', this.value);
    });
}

css.style.css:
:root {
    --primary: #6a1b9a;
    --secondary: #9c27b0;
    --dark: #121212;
    --darker: #0a0a0a;
    --light: #f5f5f5;
    --gray: #333;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
}

body {
    background-color: var(--dark);
    color: var(--light);
    line-height: 1.6;
}

.container, .player-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 0;
    border-bottom: 1px solid var(--gray);
}

.logo img {
    height: 40px;
}

.hamburger {
    font-size: 24px;
    cursor: pointer;
    color: var(--secondary);
}

.video-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 20px;
    margin-top: 30px;
}

.video-card {
    background: var(--gray);
    border-radius: 8px;
    overflow: hidden;
    transition: all 0.3s ease;
    cursor: pointer;
}

.video-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(106, 27, 154, 0.3);
}

.video-thumbnail {
    width: 100%;
    height: 160px;
    object-fit: cover;
    background: var(--darker);
}

.video-info {
    padding: 15px;
}

.video-info h3 {
    margin-bottom: 5px;
    font-size: 16px;
}

.video-info p {
    color: #aaa;
    font-size: 14px;
}

/* Player Page Styles */
.player-wrapper {
    width: 100%;
    margin: 20px 0;
}

.player-wrapper video {
    width: 100%;
    max-height: 70vh;
    background: #000;
    border-radius: 8px;
}

.quality-selector {
    margin: 15px 0;
}

.quality-selector select {
    padding: 8px 12px;
    background: var(--darker);
    color: var(--light);
    border: 1px solid var(--primary);
    border-radius: 4px;
}

.back-btn {
    display: inline-block;
    margin-top: 20px;
    padding: 10px 20px;
    background: var(--primary);
    color: white;
    text-decoration: none;
    border-radius: 4px;
    font-weight: 600;
    transition: all 0.3s ease;
}

.back-btn:hover {
    background: var(--secondary);
}

/* Modal Styles */
.modal {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0,0,0,0.8);
    z-index: 1000;
    justify-content: center;
    align-items: center;
}

.modal-content {
    background: var(--darker);
    padding: 30px;
    border-radius: 8px;
    width: 90%;
    max-width: 500px;
    position: relative;
}

.close-btn {
    position: absolute;
    top: 15px;
    right: 15px;
    font-size: 24px;
    cursor: pointer;
    color: var(--light);
}

.form-group {
    margin-bottom: 20px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    color: var(--light);
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 12px;
    border-radius: 4px;
    border: 1px solid var(--gray);
    background: var(--dark);
    color: var(--light);
}

.btn {
    background: var(--primary);
    color: white;
    border: none;
    padding: 12px 20px;
    border-radius: 4px;
    cursor: pointer;
    font-weight: 600;
    transition: all 0.3s ease;
}

.btn:hover {
    background: var(--secondary);
}

.delete-btn {
    background: #f44336;
}

.ad-slot {
    background: var(--darker);
    padding: 20px;
    text-align: center;
    border-radius: 8px;
    margin: 20px 0;
}

.ad-placeholder {
    padding: 30px;
    background: rgba(255,255,255,0.05);
    border-radius: 4px;
}

/* Responsive Design */
@media (max-width: 768px) {
    .video-grid {
        grid-template-columns: 1fr 1fr;
    }
}

@media (max-width: 480px) {
    .video-grid {
        grid-template-columns: 1fr;
    }

    .modal-content {
        padding: 20px;
    }
}
